import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import moment from 'moment';
import './ListOrder.css';

// 引入组件
import LoadingSpinner from '../LoadingSpinner/LoadingSpinner';
import ErrorMessage from '../ErrorMessage/ErrorMessage';
import { backend_url, currency } from '../../App';

const ListOrder = () => {
  // 状态管理
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  // 筛选状态
  const [filters, setFilters] = useState({
    orderType: '',
    statusID: '',
    fromDate: '',
    toDate: '',
    searchText: ''
  });
  
  // 分页状态
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [ordersPerPage] = useState(10);
  
  // 排序状态
  const [sortField, setSortField] = useState('orderDate');
  const [sortDirection, setSortDirection] = useState('desc');
  
  // 状态选项
  const [statusOptions, setStatusOptions] = useState({
    tangible: [],
    virtual: []
  });

  // 获取订单状态选项
  const fetchStatusOptions = async () => {
    try {
      // 获取实体产品状态
      const tangibleResponse = await axios.get(`${backend_url}/api/orders/statuses?orderType=tangible`, {
        headers: { 'auth-token': localStorage.getItem('token') }
      });
      
      // 获取虚拟产品状态
      const virtualResponse = await axios.get(`${backend_url}/api/orders/statuses?orderType=virtual`, {
        headers: { 'auth-token': localStorage.getItem('token') }
      });
      
      setStatusOptions({
        tangible: tangibleResponse.data.statuses,
        virtual: virtualResponse.data.statuses
      });
    } catch (err) {
      console.error('Error fetching status options:', err);
    }
  };

  // 获取订单列表
  const fetchOrders = async () => {
    setLoading(true);
    setError(null);
    
    try {
      // 构建查询参数
      const params = new URLSearchParams();
      if (filters.orderType) params.append('orderType', filters.orderType);
      if (filters.statusID) params.append('statusID', filters.statusID);
      if (filters.fromDate) params.append('fromDate', filters.fromDate);
      if (filters.toDate) params.append('toDate', filters.toDate);
      params.append('page', currentPage);
      params.append('limit', ordersPerPage);
      params.append('sortBy', sortField);
      params.append('order', sortDirection);
      
      const response = await axios.get(`${backend_url}/api/orders/admin/all?${params.toString()}`, {
        headers: { 'auth-token': localStorage.getItem('token') }
      });
      
      if (response.data.success) {
        setOrders(response.data.orders);
        setTotalPages(response.data.pagination?.pages || 1);
      } else {
        setError(response.data.message || 'Failed to fetch orders');
      }
    } catch (err) {
      console.error('Error fetching orders:', err);
      setError(err.response?.data?.message || 'Error connecting to server');
    } finally {
      setLoading(false);
    }
  };

  // 初始加载
  useEffect(() => {
    fetchStatusOptions();
  }, []);

  // 当筛选、页码或排序变化时重新获取订单
  useEffect(() => {
    fetchOrders();
  }, [currentPage, filters, sortField, sortDirection]);

  // 处理筛选变化
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
    setCurrentPage(1); // 重置到第一页
  };

  // 处理排序变化
  const handleSort = (field) => {
    if (field === sortField) {
      // 如果已经按该字段排序，则切换方向
      setSortDirection(prev => prev === 'asc' ? 'desc' : 'asc');
    } else {
      // 否则设置新的排序字段，默认降序
      setSortField(field);
      setSortDirection('desc');
    }
  };

  // 重置筛选器
  const resetFilters = () => {
    setFilters({
      orderType: '',
      statusID: '',
      fromDate: '',
      toDate: '',
      searchText: ''
    });
    setCurrentPage(1);
  };

  // 获取状态名称
  const getStatusName = (statusID, orderType) => {
    const statuses = orderType === 'virtual' ? statusOptions.virtual : statusOptions.tangible;
    const status = statuses.find(s => s.statusID === statusID);
    return status?.statusName || `Status ${statusID}`;
  };

  // 获取状态类
  const getStatusClass = (statusID, orderType) => {
    if (orderType === 'tangible') {
      switch(statusID) {
        case 1: return 'status-pending';
        case 2: return 'status-shipped';
        case 3: return 'status-cancelled';
        case 4: return 'status-hold';
        default: return '';
      }
    } else {
      switch(statusID) {
        case 1: return 'status-pending';
        case 2: return 'status-issued';
        case 3: return 'status-complete';
        case 4: return 'status-refunded';
        default: return '';
      }
    }
  };

  // 渲染分页控件
  const renderPagination = () => {
    const pageNumbers = [];
    
    // 显示当前页和周围的页码
    for (let i = Math.max(1, currentPage - 2); i <= Math.min(totalPages, currentPage + 2); i++) {
      pageNumbers.push(i);
    }
    
    return (
      <div className="pagination">
        <button 
          onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
          disabled={currentPage === 1}
        >
          Previous
        </button>
        
        {currentPage > 3 && (
          <>
            <button onClick={() => setCurrentPage(1)}>1</button>
            {currentPage > 4 && <span className="ellipsis">...</span>}
          </>
        )}
        
        {pageNumbers.map(number => (
          <button
            key={number}
            onClick={() => setCurrentPage(number)}
            className={currentPage === number ? 'active' : ''}
          >
            {number}
          </button>
        ))}
        
        {currentPage < totalPages - 2 && (
          <>
            {currentPage < totalPages - 3 && <span className="ellipsis">...</span>}
            <button onClick={() => setCurrentPage(totalPages)}>{totalPages}</button>
          </>
        )}
        
        <button
          onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
          disabled={currentPage === totalPages}
        >
          Next
        </button>
      </div>
    );
  };

  // 筛选和搜索订单
  const filterOrders = () => {
    if (!filters.searchText) return orders;
    
    const searchTerm = filters.searchText.toLowerCase();
    
    return orders.filter(order => 
      order.orderID.toString().includes(searchTerm) ||
      order.items.some(item => item.productName.toLowerCase().includes(searchTerm)) ||
      getStatusName(order.statusID, order.orderType).toLowerCase().includes(searchTerm)
    );
  };

  // 渲染订单项目列表
  const renderOrderItems = (items) => {
    if (!items || items.length === 0) return <p>No items</p>;
    
    // 如果项目数量超过3个，则只显示前3个
    const displayItems = items.slice(0, 3);
    const remainingCount = items.length - 3;
    
    return (
      <div className="order-items">
        {displayItems.map((item, index) => (
          <div key={index} className="order-item">
            <span className="item-name">{item.productName}</span>
            <span className="item-quantity">x{item.quantity}</span>
            <span className="item-price">{currency}{item.price}</span>
          </div>
        ))}
        {remainingCount > 0 && (
          <div className="more-items">+{remainingCount} more items</div>
        )}
      </div>
    );
  };

  const filteredOrders = filterOrders();

  return (
    <div className="orders-list-container">
      <h1>Manage Orders</h1>
      
      {/* 筛选器 */}
      <div className="filters-container">
        <div className="filter-row">
          <div className="filter-group">
            <label>Order Type</label>
            <select 
              name="orderType" 
              value={filters.orderType} 
              onChange={handleFilterChange}
            >
              <option value="">All Types</option>
              <option value="tangible">Tangible Products</option>
              <option value="virtual">Virtual Products</option>
            </select>
          </div>
          
          <div className="filter-group">
            <label>Status</label>
            <select 
              name="statusID" 
              value={filters.statusID} 
              onChange={handleFilterChange}
            >
              <option value="">All Statuses</option>
              {filters.orderType === 'virtual' 
                ? statusOptions.virtual.map(status => (
                    <option key={status.statusID} value={status.statusID}>
                      {status.statusName}
                    </option>
                  ))
                : statusOptions.tangible.map(status => (
                    <option key={status.statusID} value={status.statusID}>
                      {status.statusName}
                    </option>
                  ))
              }
            </select>
          </div>
          
          <div className="filter-group">
            <label>From Date</label>
            <input 
              type="date" 
              name="fromDate" 
              value={filters.fromDate} 
              onChange={handleFilterChange}
            />
          </div>
          
          <div className="filter-group">
            <label>To Date</label>
            <input 
              type="date" 
              name="toDate" 
              value={filters.toDate} 
              onChange={handleFilterChange}
            />
          </div>
        </div>
        
        <div className="filter-row">
          <div className="filter-group search-group">
            <input 
              type="text" 
              name="searchText" 
              value={filters.searchText} 
              onChange={handleFilterChange}
              placeholder="Search by order ID, product name, or status..." 
            />
          </div>
          
          <button className="reset-button" onClick={resetFilters}>
            Reset Filters
          </button>
        </div>
      </div>
      
      {/* 错误信息 */}
      {error && <ErrorMessage message={error} />}
      
      {/* 加载中 */}
      {loading ? (
        <LoadingSpinner />
      ) : (
        <>
          {/* 订单表格 */}
          {filteredOrders.length > 0 ? (
            <div className="orders-table-container">
              <table className="orders-table">
                <thead>
                  <tr>
                    <th onClick={() => handleSort('orderID')} className="sortable">
                      Order ID
                      {sortField === 'orderID' && (
                        <span className="sort-indicator">
                          {sortDirection === 'asc' ? ' ▲' : ' ▼'}
                        </span>
                      )}
                    </th>
                    <th onClick={() => handleSort('orderDate')} className="sortable">
                      Order Date
                      {sortField === 'orderDate' && (
                        <span className="sort-indicator">
                          {sortDirection === 'asc' ? ' ▲' : ' ▼'}
                        </span>
                      )}
                    </th>
                    <th>Customer</th>
                    <th>Items</th>
                    <th onClick={() => handleSort('totalAmount')} className="sortable">
                      Total
                      {sortField === 'totalAmount' && (
                        <span className="sort-indicator">
                          {sortDirection === 'asc' ? ' ▲' : ' ▼'}
                        </span>
                      )}
                    </th>
                    <th>Type</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredOrders.map(order => (
                    <tr key={order.orderID}>
                      <td>{order.orderID}</td>
                      <td>{moment(order.orderDate).format('YYYY-MM-DD HH:mm')}</td>
                      <td>User ID: {order.userID}</td>
                      <td>{renderOrderItems(order.items)}</td>
                      <td className="total-amount">{currency}{order.totalAmount.toFixed(2)}</td>
                      <td>{order.orderType === 'tangible' ? 'Tangible' : 'Virtual'}</td>
                      <td>
                        <span className={`status-badge ${getStatusClass(order.statusID, order.orderType)}`}>
                          {getStatusName(order.statusID, order.orderType)}
                        </span>
                      </td>
                      <td>
                        <div className="action-buttons">
                          <Link 
                            to={`/admin/orders/${order.orderID}`} 
                            className="view-button"
                          >
                            View
                          </Link>
                          <Link 
                            to={`/admin/orders/${order.orderID}/edit`}
                            className="edit-button"
                          >
                            Edit
                          </Link>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="no-orders">
              <p>No orders found matching your criteria.</p>
            </div>
          )}
          
          {/* 分页 */}
          {filteredOrders.length > 0 && renderPagination()}
        </>
      )}
    </div>
  );
};

export default ListOrder;
